presets.init_eqs_eel:
t0a = time; t0 = time+.5;
//sdev = .01;
//spb_ = .5; spb = .51;
p1 = 0; p2 = 0;
height = 1.5;
speed = .2;
p1 = rand(100); p2 = rand(100); //starting pos
sidestep = (rand(10)-5)/20;

presets.frame_eqs_eel:
dec_m = pow (0.85, 30/fps); dec_s = pow (0.96, 30/fps); dec_f = pow (0.7, 30/fps);

beat = bass+mid+treb; 
avg = avg*dec_s + beat*(1-dec_s);
is_beat = above(beat, .2+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_m;
index = (index + is_beat) %16;
index2 = (index2 + is_beat*bnot(index))%8;
hpeak = hpeak*dec_f + above(peak,6)*is_beat;

q20 = vol_;
q22 = max(0,peak-.0)+.01;
q24 = is_beat;
q27 = index +1;

//*********** Tele****************
tele = 18*(sin(time/14+(p1-p2)/73)-.75);
q29 = max(min(tele,1),0);
ob_size= (q29 - max(min(tele-1,1),0))/2 ;
q29 = q29*3+1;

trig = is_beat*below(rand(100),2*avg);
ran1 = ran1 + trig*(rand(100)/50-1)*1; //dir

trig = is_beat*below(rand(100),10);
ran2 = ran2*bnot(trig) + trig*(rand(100)/50-1.1); //vdir
monitor = ran2;
trig = is_beat*below(rand(100),5*avg);
ran3 = ran3*bnot(trig) + trig*(avg/32+.05); //speed

speed = speed + 1/fps*(max(min(ran3+(q29-1)/6-speed,.05),-.05));
//speed = -.0;
rota =  0;

ddir = ddir *dec_s + (1-dec_s)*(ran1*2-dir);
//ddir = 61/fps; vdir = 0;
ddir_ = ddir_ *dec_s + ddir *(1-dec_s);
dir = dir + ddir_/fps*abs(speed) ;
//dir = 0;
//dir = dir + sin(time/2)/fps*above(sin(time/2),0);
q9 = dir;

vdir1 = vdir1*dec_s + (1-dec_s)*(ran2)/fps*8;
vdir = vdir*dec_s + (1-dec_s)*vdir1;
vdir =  if (below(vdir,0), vdir*if(below(height,3),1-pow(height-3,2)/4,1),vdir);
vdir =  if (above(vdir,0), vdir*if(above(height,4),1-pow(height-4,2)/4,1),vdir);

height = height + speed/fps * vdir*16;
height = min(max(height,1),6);

q7 = vdir; //Blick vertikal; verschiebt nur uv1.y
q8 = if (above(vdir,0),height,height-4*vdir); //Flughoehe; 


//***** Tilt
//q1 = cos(ddir*20*speed);
//q2 = sin(-ddir*20*speed);
//q1 = 1; q2 = 0;
//***** moving direction

q3 = cos(dir);
q4 = sin(dir);

//***** x-y-Position
p1 = p1 + q3/fps*speed*cos(vdir);
p2 = p2 + q4/fps*speed*cos(vdir);

q5 = -p2+time*sidestep;
q6 = p1;

//*******landscape
q10 = .4*sin(time/77+(p1+p2)/47)-.1; // cloud density, -1 bis .5
q11 = .8 + cos(p1/11)/3; //rock smooth/-brightness, 0.5 - 3; //use for daylight ?#
q12 = 0.5 + .3 * sin(time/22+p1/37); //hormask;
q13 = .1+.3*sin(p1/3); // glow; -.4 - max ca 0.4, nicht ersetzen durch lavmod2
q14 =  (1+cos(p2/5))/3;  //lava structure harshness, 0 bis 1
q30 = .3*(cos(time/75+p2/123)+1); //horizon brightness (moon effect)
q31 = .2 + .1 * sin(time/94); //cloud size
q32 = -.2-sin(time/22)/4; //Planet Y
//****** daytime
q15 = abs(sin(time/45+p1/87))-.75; //daytime

// q10 = -.4;

//******sunpos xy
q16 =  0.2/height; //sky_gnd

q17 = tan(dir/2);
q18 = q16-q15;
//*************** flash ****************
q21 = min(hpeak,1) * rand(10)/10 * below(q15,-.4) * above(q10,.1);

//side = below(speed,.3)*below(q29,2)*below(ddir_,.1)*(index2%3-1);
rot = -ddir_*speed/2 + rota;
dx = .5; dy = .25+q7 ;

