t_abs = sample;
t_rel = sample-time/6;
ampl = sin(t_abs*3)-0.0;

ox = 3*sin (time) + ampl*(sin (t_rel*267)) ;
oy = 3*cos (time) + ampl*(cos (t_rel*277)) ;
oz = 0*cos (t_rel*time/23)+t_abs*4 ;


