//Camera speed and rotational speed is limited, camera lags behind
//Camera angle is forced back into horizontal position

//-------------- BEGIN MAIN PARAMS ---------------
//SPEEDS
gSpeed = .65;   //global speed, affects all other speeds incl. particle lifetimes
hSpeed = .03;  //firework head speed
pSpeed = .06;   //speed of primary particles
tSpeed = hspeed*3.5;  //speed of tail particles

//CAMERA
CamDist  = .03; //distance camera tries to stay behind firework 
CamSpeed = .27;  //camera follower speed
CamLock = 6;    //how fast does camera angle lock on firework
HLock = .1;     //force camera angle back into horizontal view

//OTHER PARAMS
sharp = 40;    //sharpness of bass/treb triggered head direction changes
g0 = .02;      //gravitation
cw = 50;       //air drag

//---------- END MAIN PARAMS -----------------

n = 0; loop (150000,gmegabuf(n) = 0; n+=1);
fac = .4; reg19 = fac;

//SPARKS
obj_num = 1400; reg00 = obj_num;
obj_size = 16; reg01 = obj_size;
max_age = 3.8; reg02 = max_age;
head_ptr = 0; 
free = 0;

//SPARKS2
num2 = 2048; reg20 = num2;
free2 = 0;
ptr2 = 0;
mage2 = 1; reg22 = mage2;
base2 = 100000;

//TAIL
t_num = 800; reg10 =t_num;
t_age = 1; reg12 = t_age;
tbase = obj_num*obj_size; reg13 = tbase;
t_ptr = 0; 
t_free = 0;


x0 = (rand(10)-5)*.03; y0 = (rand(10)-5)*.03; z0 = (rand(10)-5)*.03;;
xcam = 0; ycam = .0; zcam = -0;
//Obj Structure - Index
//0..2: x,y,z  3..5: vx,vy,vz  6..8: fx,fy,fz
